import React, { useState, useEffect } from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

function Items() {
  const useStyles = makeStyles((theme) => ({
    root: {
      width: '40%',
      marginTop: '2%',
      overflowX: 'auto',
      // margin: 'auto',
      marginLeft: '5%',
    },
  }));
  useEffect(() => {
    getItems();
  }, []);

  const classes = useStyles();

  const [itemsFetched, setItems] = useState([]);

  // Fetch items from database
  const getItems = async () => {
    try {
      const itemsFetched = await axios.get(`/items`);
      //   if (itemsFetched.data.length > 0 || itemsFetched.data.length === 0) {
      //     setLoading(false);
      //   }
      console.log(itemsFetched);
      setItems([...itemsFetched.data]);
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <Paper className={classes.root}>
      <Table className={classes.table}>
        <TableHead>
          <TableRow>
            <TableCell>Items into the fridge</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {itemsFetched.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  );
}

export default Items;
