import React, { useEffect, useState } from 'react';
import Schedule from './components/Schedule';
import Navbar from './components/Navbar';
import Items from './components/Items';
import axios from 'axios';

import './App.css';

function App() {
  const [workersFetched, setWorkers] = useState([]);
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    getWorkers();
  }, []);

  // Fetch workers from database
  const getWorkers = async () => {
    try {
      const workersFetched = await axios.get(`/workers`);
      if (workersFetched.data.length > 0 || workersFetched.data.length === 0) {
        setLoading(false);
      }
      setWorkers([...workersFetched.data]);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <Navbar />
      <Schedule workersFetched={workersFetched} />
    </div>
  );
}

export default App;
