import React, { useState, useEffect } from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';

function Items() {
  const useStyles = makeStyles((theme) => ({
    root: {
      width: '40%',
      marginTop: '2%',
      overflowX: 'auto',
      // margin: 'auto',
      marginLeft: '5%',
    },
  }));
  useEffect(() => {
    getItems();
  }, []);

  const classes = useStyles();

  const [itemsFetched, setItems] = useState([]);

  // Fetch items from database
  const getItems = async () => {
    try {
      const itemsFetched = await axios.get(`/items`);
      if (itemsFetched.data.length > 0 || itemsFetched.data.length === 0) {
        setLoading(false);
      }
      console.log(itemsFetched);
      setItems([...itemsFetched.data]);
    } catch (error) {
      console.log(error);
    }
  };
  return <Paper className={classes.root}>Fridge</Paper>;
}

export default Items;
