import React, { useState, useEffect } from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

function Items() {
  const useStyles = makeStyles((theme) => ({
    root: {
      marginTop: '2%',
      overflowX: 'auto',
      // margin: 'auto',
      marginLeft: '5%',
    },
    title: {
      textAlign: 'center',
    },
    items: {
      width: '50%',
    },
  }));
  useEffect(() => {
    getItems();
  }, []);

  const classes = useStyles();

  const [itemsFetched, setItems] = useState([]);

  // Fetch items from database
  const getItems = async () => {
    try {
      const itemsFetched = await axios.get(`/items`);
      //   if (itemsFetched.data.length > 0 || itemsFetched.data.length === 0) {
      //     setLoading(false);
      //   }
      console.log(itemsFetched);
      setItems([...itemsFetched.data]);
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <div className={classes.items}>
      <h1 className={classes.title}> Items into the fridge </h1>
      <Paper className={classes.root}>
        <Table className={classes.table}>
          <TableBody>
            {itemsFetched.map((row) => (
              <TableRow key={row.name}>
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell>
                  <IconButton
                    aria-label="delete"
                    // className={classes.margin}
                    // onClick={(e) => deleteWorker(row._id)}
                  >
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </div>
  );
}

export default Items;
